parameters.jsCode:
const items = $input.all();

// Return full JSON so we can see exact structure
return items;

const message = items[0].json;

// Decode Gmail Base64 + Quoted-Printable
function decodeBase64QP(input) {
  let b64 = input.replace(/-/g, '+').replace(/_/g, '/');
  let buf = Buffer.from(b64, 'base64').toString('utf8');
  buf = buf.replace(/=\r?\n/g, ""); // remove soft line breaks
  buf = buf.replace(/=([A-Fa-f0-9]{2})/g, (_, hex) =>
    String.fromCharCode(parseInt(hex, 16))
  );
  return buf;
}

// Extract text from nested parts
function extractParts(parts) {
  for (const part of parts) {
    if ((part.mimeType === "text/plain" || part.mimeType === "text/html") 
        && part.body?.data) {
      return decodeBase64QP(part.body.data);
    }
    if (part.parts) {
      const inner = extractParts(part.parts);
      if (inner) return inner;
    }
  }
  return null;
}

// Clean extracted email text
function cleanEmailText(rawText) {
  let text = rawText;

  // Remove forwarded/reply headers
  text = text.replace(/---------- Forwarded message ---------|On .* wrote:|From:.*|To:.*|Date:.*|Subject:.*/gi, "");

  // Remove common disclaimers
  text = text.replace(/Disclaimer:.*$/gis, "");
  text = text.replace(/This message was sent from.*$/gis, "");

  // Remove multiple line breaks
  text = text.replace(/\n\s*\n\s*\n+\r/g, " ");
  text = text.replace(/\r\n\r\n/g, " ");

  // Trim
  return text.trim();
}

// Main Logic
let body = "";
if (message.payload) {
  if (message.payload.body?.data) {
    body = decodeBase64QP(message.payload.body.data);
  } else if (message.payload.parts) {
    body = extractParts(message.payload.parts) || "";
  }
}

const cleaned = cleanEmailText(body || "No body found");

return [{ json: { text: cleaned } }];

// Input JSON example:
// {
//   "test_date": "2025-07-23",
//   "test_time": "07:00 PM",
//   "last_date_to_apply": "2025-08-24",
//   "last_time_to_apply": "08:00 PM",
//   "test_name": "Meesho Online Test",
//   "event_type": "Test"
// }

const eventType = $json.event_type || "Placement Drive";
const title = $json.company ? `${$json.company} ${eventType}` : eventType;
const summary = $json.summary || "No summary available";

// Determine which date to use
let dateStr = $json.test_date || $json.last_date_to_apply;
let timeStr = $json.test_time || $json.last_time_to_apply;

let startDateTime, endDateTime, startDate, endDate;

if (dateStr) {
  let [year, month, day] = dateStr.split("-");
  if (year.length === 2) year = "20" + year;

  if (timeStr) {
    const [hm, ampm] = timeStr.split(" ");
    let [h, m] = hm.split(":").map(Number);
    if (ampm?.toUpperCase() === "PM" && h !== 12) h += 12;
    if (ampm?.toUpperCase() === "AM" && h === 12) h = 0;

    startDateTime = `${year}-${("0"+month).slice(-2)}-${("0"+day).slice(-2)}T${("0"+h).slice(-2)}:${("0"+m).slice(-2)}:00+05:30`;
    const endHour = (h + 1) % 24;
    endDateTime = `${year}-${("0"+month).slice(-2)}-${("0"+day).slice(-2)}T${("0"+endHour).slice(-2)}:${("0"+m).slice(-2)}:00+05:30`;
  } else {
    startDate = `${year}-${("0"+month).slice(-2)}-${("0"+day).slice(-2)}`;
    const jsDate = new Date(`${year}-${month}-${day}T00:00:00`);
    jsDate.setDate(jsDate.getDate() + 1);
    const endY = jsDate.getFullYear();
    const endM = ("0"+(jsDate.getMonth()+1)).slice(-2);
    const endD = ("0"+jsDate.getDate()).slice(-2);
    endDate = `${endY}-${endM}-${endD}`;
  }
}

return {
  json: {
    title,           // e.g., "Honeywell Test"
    summary,
    eventType,       // Test / PPT / Placement
    startDateTime,
    endDateTime,
    startDate,
    endDate
  }
};

